
var timeout=500;var prefix="https";$(function(){imgur_direct_worker()});function imgur_direct_worker(){$(".post-image-container").each(function(){var a=$(this);if(!has_direct_link(a)&&is_image_ready(a)){write_direct_link(a)}});setTimeout(imgur_direct_worker,timeout)}function get_direct_link(a){if(a.find(".zoom").length>0){var b=a.find(".image .zoom").attr("href")}else{var b=a.find(".image img").attr("src")}b="https:"+b;return b}function is_image_ready(a){var b=get_direct_link(a);return b.indexOf("undefined")==-1&&b.indexOf("blob")==-1}function has_direct_link(a){return a.find(".direct").length>0}function write_direct_link(a){var c={width:"100%",background:"#2C2F34","text-align":"center","font-weight":"bold","font-size":"0.8em",height:"28px","margin-bottom":"0px"};var b=$('<textarea onclick="$(this).select()">'+get_direct_link(a)+"</textarea>");b.addClass("direct").css(c);a.prepend(b)};