
create_direct();function create_direct(){$(".post-image-container").each(function(){var a=$(this);if(!has_direct_link(a)&&is_image_ready(a)){write_direct_link(a)}});setTimeout(create_direct,500)}function get_direct_link(a){var b=a.find("a.zoom").attr("href");b="http:"+b;return b}function is_image_ready(a){var b=get_direct_link(a);return b.indexOf("undefined")==-1&&b.indexOf("blob")==-1}function has_direct_link(a){return a.find(".direct").length>0}function write_direct_link(a){var c={width:"100%",background:"#2C2F34","text-align":"center","font-weight":"bold","font-size":"0.8em",height:"28px","margin-bottom":"0px"};var b=$('<textarea onclick="$(this).select()">'+get_direct_link(a)+"</textarea>");b.addClass("direct").css(c);a.prepend(b)};